package DataTypes;

public class Int {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		// Initization of Data type
		int bikespeed;
		
		//Declarizaion of Data type
		bikespeed = 50;
		
		System.out.println("My Bike Speed is: "+ bikespeed);
		
		
		bikespeed = 50 + 20;
		
		System.out.println("My Bike Speed is: "+ bikespeed);
		
		
	}

}
